import React, {useEffect, useState} from 'react'
import { View, TextInput, ScrollView, StyleSheet, Button ,ActivityIndicator} from 'react-native'
import {firebase} from '../context/FirebaseContext'

const UserDetailScreen=(props)=> {
 // console.log(props.route.params.userId)

const initialState={ id:'', fullName:'',email:'', age:'', gender:'',departmentId:'',reason:''}

const [candidate, setCandidate] = useState(initialState)
const [loading, setloading] = useState(true)


useEffect(()=>{
  getCandidateById(props.route.params.userId);
},[])

const getCandidateById =async(id) =>{
  const dbRef =  firebase.firestore().collection("registration").doc(id)
  const doc =  await dbRef.get();
  const candidate = doc.data();
  console.log(candidate, 'fire useEffect')
  setCandidate({
      ...candidate,
      id:doc.id
  })
  setloading(false)
}

  const handleChangeText =(name, value)=>{
    setCandidate({...candidate, [name]:value});
    console.log('handle change fire' ,[name])
  };

  if(loading) {
    return (
      <View>
        <ActivityIndicator size="large" color="#9e9e9e"/>
      </View>
    )
  }

  return (
    <View style={styles.container}>
    <ScrollView style={styles.container}>
        {/* Name Input */}
        <View style={styles.inputGroup}>
        <TextInput
            placeholder="名前"
            value={candidate.fullName}
            onChangeText={(text) => { this.setState({ fullName: text})}}
        />
        </View>

        {/* Email Input */}
        <View style={styles.inputGroup}>
        <TextInput
            placeholder="メール"
            multiline={true}
            numberOfLines={4}
            value={candidate.email}
            onChangeText={(value) => handleChangeText(value, "email")}  
        />
        </View>

        {/* Input */}
        <View style={styles.inputGroup}>
        <TextInput
            placeholder="年齢"
            value={candidate.age}
            onChangeText={(value) => handleChangeText(value, "age")}
        />
        </View>

        {/* Input */}
       <View style={styles.inputGroup}>
        <TextInput
            placeholder="性別"
            value={candidate.gender}
            onChangeText={(value) => handleChangeText(value, "gender")}
        />
        </View>

           {/* Input */}
       <View style={styles.inputGroup}>
        <TextInput
            placeholder="部署ID"
            value={candidate.departmentId}
            onChangeText={(value) => handleChangeText(value, "departmentId")}

        />
        </View>

            {/* Input */}
       <View style={styles.inputGroup}>
        <TextInput
            placeholder="求職理由"
            value={candidate.reason}
            multiline={true}
            numberOfLines={50}
            onChangeText={(value) => handleChangeText(value, "reason")}

        />
        </View>

        <View style={styles.button}>
        <Button 
        color="#E37399"
        title="Update User" onPress={() => updateUser()} />

        </View>

        <View style={styles.button}>
        <Button 
        color="#E37396"
        title="Delete User" onPress={() => openConfirmationAlear()} />
        </View>
    </ScrollView>
    </View>



    )
}
export default UserDetailScreen



const styles = StyleSheet.create({
    container: {
      flex: 1,
      padding: 35,
    },
    inputGroup: {
      flex: 1,
      padding: 0,
      marginBottom: 15,
      borderBottomWidth: 1,
      borderBottomColor: "#cccccc",
    },
    loader: {
      left: 0,
      right: 0,
      top: 0,
      bottom: 0,
      position: "absolute",
      alignItems: "center",
      justifyContent: "center",
    },
  });
